import React, { useState, useMemo } from 'react';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  BarChart,
  Bar,
  PieChart,
  Pie,
  Cell,
} from 'recharts';
import { Card, CardContent, CardHeader, CardTitle } from './components/ui/card';
import { Button } from './components/ui/button';

// Revenue by Customer Component
interface RevenueByCustomerProps {
  revenueByCustomer: Array<{ customer: string; revenue: number }>;
}

const RevenueByCustomerComponent: React.FC<RevenueByCustomerProps> = ({ revenueByCustomer }) => {
  const [showAll, setShowAll] = useState(false);

  const topCustomers = revenueByCustomer.slice(0, 5);
  const remainingCustomers = revenueByCustomer.slice(5);
  const totalRevenue = revenueByCustomer.reduce((sum, customer) => sum + customer.revenue, 0);

  const formatRevenue = (revenue: number) => {
    return `$${revenue.toLocaleString()}`;
  };

  const getPercentage = (revenue: number) => {
    return ((revenue / totalRevenue) * 100).toFixed(1);
  };

  return (
    <div className="space-y-4">
      {/* Top 5 Customers */}
      <div className="space-y-3">
        {topCustomers.map((customer, index) => (
          <div key={customer.customer} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
            <div className="flex items-center space-x-3">
              <div className="flex items-center justify-center w-8 h-8 bg-blue-100 text-blue-600 rounded-full font-semibold text-sm">
                {index + 1}
              </div>
              <div>
                <div className="font-medium text-gray-900">{customer.customer}</div>
                <div className="text-sm text-gray-500">{getPercentage(customer.revenue)}% of total</div>
              </div>
            </div>
            <div className="text-right">
              <div className="font-semibold text-green-600">{formatRevenue(customer.revenue)}</div>
            </div>
          </div>
        ))}
      </div>

      {/* Show More/Less Button */}
      {remainingCustomers.length > 0 && (
        <div className="border-t pt-4">
          <Button
            variant="outline"
            onClick={() => setShowAll(!showAll)}
            className="w-full text-sm"
          >
            {showAll ? 'Show Less' : `Show ${remainingCustomers.length} More Customers`}
          </Button>
        </div>
      )}

      {/* Remaining Customers (Collapsible) */}
      {showAll && remainingCustomers.length > 0 && (
        <div className="space-y-2 border-t pt-4">
          {remainingCustomers.map((customer, index) => (
            <div key={customer.customer} className="flex items-center justify-between p-2 bg-gray-25 rounded">
              <div className="flex items-center space-x-3">
                <div className="flex items-center justify-center w-6 h-6 bg-gray-100 text-gray-600 rounded-full font-medium text-xs">
                  {index + 6}
                </div>
                <div>
                  <div className="text-sm font-medium text-gray-700">{customer.customer}</div>
                  <div className="text-xs text-gray-500">{getPercentage(customer.revenue)}% of total</div>
                </div>
              </div>
              <div className="text-right">
                <div className="text-sm font-medium text-green-600">{formatRevenue(customer.revenue)}</div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Summary Stats */}
      <div className="grid grid-cols-2 gap-4 pt-4 border-t">
        <div className="text-center">
          <div className="text-2xl font-bold text-blue-600">{revenueByCustomer.length}</div>
          <div className="text-sm text-gray-500">Total Customers</div>
        </div>
        <div className="text-center">
          <div className="text-2xl font-bold text-green-600">{formatRevenue(totalRevenue)}</div>
          <div className="text-sm text-gray-500">Total Revenue</div>
        </div>
      </div>
    </div>
  );
};

// Revenue by Product Component
interface RevenueByProductProps {
  revenueByProduct: Array<{ product: string; revenue: number }>;
}

const RevenueByProductComponent: React.FC<RevenueByProductProps> = ({ revenueByProduct }) => {
  const [showAll, setShowAll] = useState(false);

  const topProducts = revenueByProduct.slice(0, 5);
  const remainingProducts = revenueByProduct.slice(5);
  const totalRevenue = revenueByProduct.reduce((sum, product) => sum + product.revenue, 0);

  const formatRevenue = (revenue: number) => {
    return `$${revenue.toLocaleString()}`;
  };

  const getPercentage = (revenue: number) => {
    return ((revenue / totalRevenue) * 100).toFixed(1);
  };

  return (
    <div className="space-y-4">
      {/* Top 5 Products */}
      <div className="space-y-3">
        {topProducts.map((product, index) => (
          <div key={product.product} className="flex items-center justify-between p-3 bg-orange-50 rounded-lg">
            <div className="flex items-center space-x-3">
              <div className="flex items-center justify-center w-8 h-8 bg-orange-100 text-orange-600 rounded-full font-semibold text-sm">
                {index + 1}
              </div>
              <div>
                <div className="font-medium text-gray-900">{product.product}</div>
                <div className="text-sm text-gray-500">{getPercentage(product.revenue)}% of total</div>
              </div>
            </div>
            <div className="text-right">
              <div className="font-semibold text-green-600">{formatRevenue(product.revenue)}</div>
            </div>
          </div>
        ))}
      </div>

      {/* Show More/Less Button */}
      {remainingProducts.length > 0 && (
        <div className="border-t pt-4">
          <Button
            variant="outline"
            onClick={() => setShowAll(!showAll)}
            className="w-full text-sm"
          >
            {showAll ? 'Show Less' : `Show ${remainingProducts.length} More Products`}
          </Button>
        </div>
      )}

      {/* Remaining Products (Collapsible) */}
      {showAll && remainingProducts.length > 0 && (
        <div className="space-y-2 border-t pt-4">
          {remainingProducts.map((product, index) => (
            <div key={product.product} className="flex items-center justify-between p-2 bg-gray-25 rounded">
              <div className="flex items-center space-x-3">
                <div className="flex items-center justify-center w-6 h-6 bg-gray-100 text-gray-600 rounded-full font-medium text-xs">
                  {index + 6}
                </div>
                <div>
                  <div className="text-sm font-medium text-gray-700">{product.product}</div>
                  <div className="text-xs text-gray-500">{getPercentage(product.revenue)}% of total</div>
                </div>
              </div>
              <div className="text-right">
                <div className="text-sm font-medium text-green-600">{formatRevenue(product.revenue)}</div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Summary Stats */}
      <div className="grid grid-cols-2 gap-4 pt-4 border-t">
        <div className="text-center">
          <div className="text-2xl font-bold text-orange-600">{revenueByProduct.length}</div>
          <div className="text-sm text-gray-500">Total Products</div>
        </div>
        <div className="text-center">
          <div className="text-2xl font-bold text-green-600">{formatRevenue(totalRevenue)}</div>
          <div className="text-sm text-gray-500">Total Revenue</div>
        </div>
      </div>
    </div>
  );
};

