import React, { useState, useMemo } from 'react';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
} from 'recharts';
import { Card, CardContent, CardHeader, CardTitle } from './components/ui/card';
import { Button } from './components/ui/button';
import AlpineReportUpload from './components/AlpineReportUpload';
import CustomerDetailModal from './components/CustomerDetailModal';
import { AlpineSalesRecord, CustomerProgressAnalysis } from './utils/alpineParser';

// Revenue by Customer Component
interface RevenueByCustomerProps {
  revenueByCustomer: Array<{ customer: string; fullCustomerName: string; revenue: number }>;
  alpineData: AlpineSalesRecord[];
  onCustomerClick?: (customerName: string) => void;
  isComparisonMode?: boolean;
}

const RevenueByCustomerComponent: React.FC<RevenueByCustomerProps> = ({ 
  revenueByCustomer, 
  alpineData,
  onCustomerClick,
  isComparisonMode = false 
}) => {
  const [showAll, setShowAll] = useState(false);

  const topCustomers = revenueByCustomer.slice(0, 5);
  const remainingCustomers = revenueByCustomer.slice(5);
  const totalRevenue = revenueByCustomer.reduce((sum, customer) => sum + customer.revenue, 0);

  const formatRevenue = (revenue: number) => {
    return `$${revenue.toLocaleString()}`;
  };

  const getPercentage = (revenue: number) => {
    return ((revenue / totalRevenue) * 100).toFixed(1);
  };

  // Get top products for a customer
  const getTopProductsForCustomer = (customerName: string) => {
    const customerSales = alpineData.filter(sale => sale.customerName === customerName);
    const productRevenue = customerSales.reduce((acc, sale) => {
      acc[sale.productName] = (acc[sale.productName] || 0) + sale.revenue;
      return acc;
    }, {} as Record<string, number>);

    return Object.entries(productRevenue)
      .sort(([,a], [,b]) => b - a)
      .slice(0, 5)
      .map(([product, revenue], index) => ({
        id: `${customerName}-${product}-{$index}`,
        product: product.length > 20 ? product.substring(0, 20) + '...' : product,
        fullProduct: product,
        revenue: Math.round(revenue * 100) / 100,
      }));
  };

  const handleCustomerClick = (fullCustomerName: string) => {
    if (onCustomerClick && isComparisonMode) {
      onCustomerClick(fullCustomerName);
    }
  };

  return (
    <div className="space-y-4">
      <h3 className="text-lg font-semibold text-gray-900">Top Customers</h3>

      {/* Top 5 Customers */}
      <div className="space-y-3">
        {topCustomers.map((customer) => (
          <div 
            key={customer.customer}
            className={`flex items-center justify-between p-3 bg-gray-50 rounded-lg hover:bg-gray-100 transition-colors ${
              isComparisonMode ? 'cursor-pointer' : ''
            }`}
            onClick={() => handleCustomerClick(customer.fullCustomerName)}
            title={isComparisonMode ? "Click to see detailed comparison" : "Customer revenue"}
          >
            <div className="flex-1 min-w-0">
              <div className="text-sm font-medium text-gray-900 truncate">
                  {customer.customer}
                </div>
              <div className="text-xs text-gray-500">{getPercentage(customer.revenue)}% of total</div>
              </div>
            <div className="text-sm font-semibold text-gray-900">
              {formatRevenue(customer.revenue)}
            </div>
          </div>
        ))}
      </div>

      {/* Remaining Customers */}
      {remainingCustomers.length > 0 && (
        <>
          {showAll && (
            <div className="space-y-3">
              {remainingCustomers.map((customer) => (
                <div 
                  key={customer.customer}
                  className={`flex items-center justify-between p-3 bg-gray-50 rounded-lg hover:bg-gray-100 transition-colors ${
                    isComparisonMode ? 'cursor-pointer' : ''
                  }`}
                  onClick={() => handleCustomerClick(customer.fullCustomerName)}
                  title={isComparisonMode ? "Click to see detailed comparison" : "Customer revenue"}
                >
                  <div className="flex-1 min-w-0">
                    <div className="text-sm font-medium text-gray-900 truncate">
                    {customer.customer}
                  </div>
                  <div className="text-xs text-gray-500">{getPercentage(customer.revenue)}% of total</div>
                </div>
                  <div className="text-sm font-semibold text-gray-900">
                    {formatRevenue(customer.revenue)}
              </div>
            </div>
          ))}
        </div>
      )}

          <Button
            variant="outline"
            size="sm"
            onClick={() => setShowAll(!showAll)}
            className="w-full"
          >
            {showAll ? 'Show Less' : `Show ${remainingCustomers.length} More`}
          </Button>
        </>
      )}
    </div>
  );
};

// Revenue by Product Component
interface RevenueByProductProps {
  revenueByProduct: Array<{ id: string; product: string; fullProduct: string; revenue: number }>;
  alpineData: AlpineSalesRecord[];
}

const RevenueByProductComponent: React.FC<RevenueByProductProps> = ({ revenueByProduct, alpineData }) => {
  const [showAll, setShowAll] = useState(false);

  const topProducts = revenueByProduct.slice(0, 5);
  const remainingProducts = revenueByProduct.slice(5);
  const totalRevenue = revenueByProduct.reduce((sum, product) => sum + product.revenue, 0);

  const formatRevenue = (revenue: number) => {
    return `$${revenue.toLocaleString()}`;
  };

  const getPercentage = (revenue: number) => {
    return ((revenue / totalRevenue) * 100).toFixed(1);
  };

  return (
    <div className="space-y-4">
      <h3 className="text-lg font-semibold text-gray-900">Top Products</h3>

      {/* Top 5 Products */}
      <div className="space-y-3">
        {topProducts.map((product) => (
          <div 
            key={product.id}
            className="flex items-center justify-between p-3 bg-gray-50 rounded-lg hover:bg-gray-100 transition-colors"
          >
            <div className="flex-1 min-w-0">
              <div className="text-sm font-medium text-gray-900 truncate">
                  {product.product}
                </div>
              <div className="text-xs text-gray-500">{getPercentage(product.revenue)}% of total</div>
              </div>
            <div className="text-sm font-semibold text-gray-900">
              {formatRevenue(product.revenue)}
            </div>
          </div>
        ))}
      </div>

      {/* Remaining Products */}
      {remainingProducts.length > 0 && (
        <>
          {showAll && (
            <div className="space-y-3">
              {remainingProducts.map((product) => (
                <div 
                  key={product.id}
                  className="flex items-center justify-between p-3 bg-gray-50 rounded-lg hover:bg-gray-100 transition-colors"
                >
                  <div className="flex-1 min-w-0">
                    <div className="text-sm font-medium text-gray-900 truncate">
                    {product.product}
                  </div>
                  <div className="text-xs text-gray-500">{getPercentage(product.revenue)}% of total</div>
                </div>
                  <div className="text-sm font-semibold text-gray-900">
                    {formatRevenue(product.revenue)}
              </div>
            </div>
          ))}
        </div>
      )}

          <Button
            variant="outline"
            size="sm"
            onClick={() => setShowAll(!showAll)}
            className="w-full"
          >
            {showAll ? 'Show Less' : `Show ${remainingProducts.length} More`}
          </Button>
        </>
      )}
    </div>
  );
};

const Dashboard: React.FC = () => {
  // Alpine data handlers
  const handleAlpineDataParsed = ({ records, customerProgressions }: {
    records: AlpineSalesRecord[];
    customerProgressions: Map<string, CustomerProgressAnalysis>;
  }) => {
    setAlpineRecords(records);
    setAlpineCustomerProgressions(customerProgressions);
  };

  const handleClearAlpineData = () => {
    setAlpineRecords([]);
    setAlpineCustomerProgressions(new Map());
  };

  // Customer modal handlers
  const handleCustomerClick = (customerName: string) => {
    setSelectedCustomerForModal(customerName);
    setIsCustomerModalOpen(true);
  };

  const handleCloseCustomerModal = () => {
    setIsCustomerModalOpen(false);
    setSelectedCustomerForModal(null);
  };

  // State management
  const [alpineRecords, setAlpineRecords] = useState<AlpineSalesRecord[]>([]);
  const [alpineCustomerProgressions, setAlpineCustomerProgressions] = useState<Map<string, CustomerProgressAnalysis>>(new Map());
  const [selectedCustomerForModal, setSelectedCustomerForModal] = useState<string | null>(null);
  const [isCustomerModalOpen, setIsCustomerModalOpen] = useState(false);

  // Calculate KPIs
  const kpis = useMemo(() => {
    const totalRevenue = alpineRecords.reduce((sum, record) => sum + record.revenue, 0);
    const totalCases = alpineRecords.reduce((sum, record) => sum + record.cases, 0);

    // Top customer by revenue
    const customerRevenue = alpineRecords.reduce((acc, record) => {
      acc[record.customerName] = (acc[record.customerName] || 0) + record.revenue;
      return acc;
    }, {} as Record<string, number>);
    const topCustomer = Object.entries(customerRevenue).sort(([,a], [,b]) => b - a)[0];

    // Top product by revenue
    const productRevenue = alpineRecords.reduce((acc, record) => {
      acc[record.productName] = (acc[record.productName] || 0) + record.revenue;
      return acc;
    }, {} as Record<string, number>);
    const topProduct = Object.entries(productRevenue).sort(([,a], [,b]) => b - a)[0];

    return {
      totalRevenue,
      totalCases,
      topCustomer: topCustomer ? topCustomer[0] : 'N/A',
      topProduct: topProduct ? topProduct[0] : 'N/A',
    };
  }, [alpineRecords]);

  // Revenue over time data (grouped by periods)
  const revenueOverTime = useMemo(() => {
    const periodRevenue = alpineRecords.reduce((acc, record) => {
      const period = record.period;
      acc[period] = (acc[period] || 0) + record.revenue;
      return acc;
    }, {} as Record<string, number>);

    return Object.entries(periodRevenue)
      .sort(([a], [b]) => a.localeCompare(b))
      .map(([period, revenue]) => ({
        period,
        revenue: Math.round(revenue * 100) / 100,
      }));
  }, [alpineRecords]);

  // Revenue by customer data
  const revenueByCustomer = useMemo(() => {
    const customerRevenue = alpineRecords.reduce((acc, record) => {
      acc[record.customerName] = (acc[record.customerName] || 0) + record.revenue;
      return acc;
    }, {} as Record<string, number>);

    return Object.entries(customerRevenue)
      .sort(([,a], [,b]) => b - a)
      .map(([customer, revenue], index) => ({
        id: `${customer}-${index}`,
        customer: customer.length > 20 ? customer.substring(0, 20) + '...' : customer,
        fullCustomerName: customer,
        revenue: Math.round(revenue * 100) / 100,
      }));
  }, [alpineRecords]);

  // Revenue by product data
  const revenueByProduct = useMemo(() => {
    const productRevenue = alpineRecords.reduce((acc, record) => {
      acc[record.productName] = (acc[record.productName] || 0) + record.revenue;
      return acc;
    }, {} as Record<string, number>);

    return Object.entries(productRevenue)
      .sort(([,a], [,b]) => b - a)
      .map(([product, revenue], index) => ({
        id: `${product}-${index}`,
        product: product.length > 20 ? product.substring(0, 20) + '...' : product,
        fullProduct: product,
        revenue: Math.round(revenue * 100) / 100,
      }));
  }, [alpineRecords]);

  // Product mix pie chart data
  const productMix = useMemo(() => {
    const total = alpineRecords.reduce((sum, record) => sum + record.revenue, 0);

    return revenueByProduct
      .slice(0, 8) // Top 8 products
      .map((product) => ({
        name: product.product.length > 12 ? product.product.substring(0, 12) + '...' : product.product,
        value: Math.round((product.revenue / total) * 100 * 100) / 100,
      }));
  }, [revenueByProduct, alpineRecords]);

  const colors = ['#3B82F6', '#EF4444', '#10B981', '#F59E0B', '#8B5CF6', '#F97316', '#06B6D4', '#84CC16'];

  if (alpineRecords.length === 0) {
    return (
      <div className="min-h-screen bg-gray-50">
        <div className="max-w-7xl mx-auto p-6">
          <div className="mb-8">
            <h1 className="text-3xl font-bold text-gray-900">Alpine Sales Dashboard</h1>
            <p className="text-gray-600 mt-2">Track and analyze Alpine distributor sales data</p>
          </div>

          <div className="grid grid-cols-1 gap-6">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                   Upload Alpine Sales Reports
                </CardTitle>
              </CardHeader>
              <CardContent>
                <AlpineReportUpload 
                  onDataParsed={handleAlpineDataParsed}
                  onClearData={handleClearAlpineData}
                />
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto p-6">
        {/* Header */}
        <div className="mb-8 flex justify-between items-start">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Alpine Sales Dashboard</h1>
            <p className="text-gray-600 mt-2">Alpine distributor sales analysis</p>
            <p className="text-sm text-gray-500 mt-1">
              {alpineRecords.length} sales records from {new Set(alpineRecords.map(r => r.period)).size} periods
            </p>
          </div>
          <Button
            variant="outline"
            onClick={handleClearAlpineData}
            className="text-red-600 border-red-200 hover:bg-red-50"
          >
            Clear Data
          </Button>
        </div>

        {/* KPIs */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          <Card>
            <CardContent className="p-6">
              <div className="flex items-center">
                <div className="flex-1">
                  <p className="text-sm font-medium text-gray-600">Total Revenue</p>
                  <p className="text-2xl font-bold text-gray-900">
                ${kpis.totalRevenue.toLocaleString()}
                  </p>
                </div>
                <div className="text-gray-400">

                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center">
                <div className="flex-1">
                  <p className="text-sm font-medium text-gray-600">Total Cases</p>
                  <p className="text-2xl font-bold text-gray-900">
                    {kpis.totalCases.toLocaleString()}
                  </p>
                </div>
                <div className="text-gray-400">

                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center">
                <div className="flex-1">
                  <p className="text-sm font-medium text-gray-600">Top Customer</p>
                  <p className="text-lg font-bold text-gray-900 truncate" title={kpis.topCustomer}>
                {kpis.topCustomer}
                  </p>
                </div>
                <div className="text-gray-400">

                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center">
                <div className="flex-1">
                  <p className="text-sm font-medium text-gray-600">Top Product</p>
                  <p className="text-lg font-bold text-gray-900 truncate" title={kpis.topProduct}>
                {kpis.topProduct}
                  </p>
                </div>
                <div className="text-gray-400">

                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Main Charts */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
          {/* Revenue Over Time */}
          <Card>
            <CardHeader>
              <CardTitle>Revenue by Period</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="h-64">
                <ResponsiveContainer width="100%" height="100%">
                <LineChart data={revenueOverTime}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="period" />
                    <YAxis />
                  <Tooltip 
                      formatter={(value: number) => [`$${value.toLocaleString()}`, 'Revenue']}
                      labelFormatter={(label) => `Period: ${label}`}
                  />
                  <Line 
                    type="monotone" 
                    dataKey="revenue" 
                    stroke="#3B82F6" 
                    strokeWidth={2}
                      dot={{ fill: '#3B82F6', strokeWidth: 2, r: 4 }}
                  />
                </LineChart>
              </ResponsiveContainer>
              </div>
            </CardContent>
          </Card>

          {/* Product Mix */}
          <Card>
            <CardHeader>
              <CardTitle>Product Mix</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="h-64">
                <ResponsiveContainer width="100%" height="100%">
                <PieChart>
                  <Pie
                    data={productMix}
                    cx="50%"
                    cy="50%"
                      outerRadius={80}
                    dataKey="value"
                      label={({ name, value }) => `${name}: ${value}%`}
                  >
                    {productMix.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={colors[index % colors.length]} />
                    ))}
                  </Pie>
                    <Tooltip formatter={(value: number) => [`${value}%`, 'Percentage']} />
                </PieChart>
              </ResponsiveContainer>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Revenue Analysis */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
          {/* Revenue by Customer */}
          <Card>
            <CardHeader>
              <CardTitle>Revenue by Customer</CardTitle>
            </CardHeader>
            <CardContent>
              <RevenueByCustomerComponent
                revenueByCustomer={revenueByCustomer}
                alpineData={alpineRecords}
                onCustomerClick={handleCustomerClick}
                isComparisonMode={true}
              />
            </CardContent>
          </Card>

          {/* Revenue by Product */}
          <Card>
            <CardHeader>
              <CardTitle>Revenue by Product</CardTitle>
            </CardHeader>
            <CardContent>
              <RevenueByProductComponent
                revenueByProduct={revenueByProduct}
                alpineData={alpineRecords}
              />
            </CardContent>
          </Card>
        </div>

        {/* Upload Section */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
               Alpine Reports Management
            </CardTitle>
          </CardHeader>
          <CardContent>
            <AlpineReportUpload 
              onDataParsed={handleAlpineDataParsed}
              onClearData={handleClearAlpineData}
            />
          </CardContent>
        </Card>

        {/* Customer Detail Modal */}
        {selectedCustomerForModal && (
          <CustomerDetailModal
            customerName={selectedCustomerForModal}
            currentInvoices={[]} // Alpine data doesn't use invoice format
            previousInvoices={[]}
            isOpen={isCustomerModalOpen}
            onClose={handleCloseCustomerModal}
            alpineData={alpineRecords}
            progressAnalysis={alpineCustomerProgressions.get(selectedCustomerForModal)}
          />
        )}
      </div>
    </div>
  );
};

export default Dashboard;
