// Generate customer progression analysis automatically
export const generateCustomerProgressions = (records: AlpineSalesRecord[]): Map<string, CustomerProgressAnalysis> => {
  const progressions = new Map<string, CustomerProgressAnalysis>();

  // Group records by customer
  const customerGroups = records.reduce((acc, record) => {
    if (!acc[record.customerName]) {
      acc[record.customerName] = [];
    }
    acc[record.customerName].push(record);
    return acc;
  }, {} as Record<string, AlpineSalesRecord[]>);

  // Analyze each customer
  Object.entries(customerGroups).forEach(([customerName, customerRecords]) => {
    const periods = new Set(customerRecords.map(r => r.period)).size;
    const allPeriods = Array.from(new Set(customerRecords.map(r => r.period))).sort();

    // Calculate customer data by period
    const periodData = allPeriods.map(period => {
      const periodRecords = customerRecords.filter(r => r.period === period);
      const totalRevenue = periodRecords.reduce((sum, r) => sum + r.revenue, 0);
      const totalCases = periodRecords.reduce((sum, r) => sum + r.cases, 0);
      const productCount = periodRecords.length;

      // Top products for this period
      const productRevenue = periodRecords.reduce((acc, record) => {
        acc[record.productName] = (acc[record.productName] || 0) + record.revenue;
        return acc;
      }, {} as Record<string, number>);

      const topProducts = Object.entries(productRevenue)
        .sort(([,a], [,b]) => b - a)
        .slice(0, 3)
        .map(([productName, revenue]) => ({
          productName,
          revenue,
          cases: periodRecords.find(r => r.productName === productName)?.cases || 0
        }));

      return {
        period,
        totalRevenue,
        totalCases,
        productCount,
        topProducts
      };
    });

    // Calculate trends
    let revenueTrend: 'increasing' | 'decreasing' | 'stable' | 'new' = 'stable';
    let caseTrend: 'increasing' | 'decreasing' | 'stable' | 'new' = 'stable';
    let productTrend: 'expanding' | 'contracting' | 'stable' | 'new' = 'stable';
    let status: 'active' | 'declining' | 'emerging' | 'lost' = 'stable';

    if (periodData.length >= 2) {
      const earliest = periodData[0];
      const latest = periodData[periodData.length - 1];

      const revenueChange = latest.totalRevenue - earliest.totalRevenue;
      const caseChange = latest.totalCases - earliest.totalCases;
      const productChange = latest.productCount - earliest.productCount;

      revenueTrend = revenueChange > 100 ? 'increasing' : revenueChange < -100 ? 'decreasing' : 'stable';
      caseTrend = caseChange > 10 ? 'increasing' : caseChange < -10 ? 'decreasing' : 'stable';
      productTrend = productChange > 0 ? 'expanding' : productChange < 0 ? 'contracting' : 'stable';

      // Determine status
      if (revenueTrend === 'increasing' && caseTrend === 'increasing') {
        status = 'active';
      } else if (revenueTrend === 'decreasing' || caseTrend === 'decreasing') {
        status = 'declining';
      } else {
        status = 'active';
      }
    } else if (periodData.length === 1) {
      revenueTrend = 'new';
      caseTrend = 'new';
      productTrend = 'new';
      status = 'emerging';
    }

    progressions.set(customerName, {
      customerName,
      periods: periodData,
      trends: {
        revenueTrend,
        caseTrend,
        productTrend,
        status
      }
    });
  });

  return progressions;
};

// Auto-generate progressions
export const customerProgressions = generateCustomerProgressions(alpineData);
